import React, { useState, useEffect } from 'react';

const Home = () => {
    // State to control CV modal visibility
    const [isCvModalOpen, setIsCvModalOpen] = useState(false);

    // Function to open the CV preview modal
    const openCvModal = () => {
        setIsCvModalOpen(true);
    };

    // Function to close the CV preview modal
    const closeCvModal = () => {
        setIsCvModalOpen(false);
    };

    // Apply/remove blur effect when modal is open/closed
    useEffect(() => {
        if (isCvModalOpen) {
            document.body.classList.add('blurred');
        } else {
            document.body.classList.remove('blurred');
        }

        // Clean up blur effect when component unmounts
        return () => {
            document.body.classList.remove('blurred');
        };
    }, [isCvModalOpen]);

    return (
        <div className="landing">
            <h1>Hi, I'm Jiteen Mohanty, a passionate Software Developer.</h1>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
            <p>Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
            <div className="explore-button-container">
                <p>Explore my work and get to know me better!</p>
                <a className="explore-button" href="#about">About</a>
                <a className="explore-button" href="#projects">Projects</a>
            </div>

            <div className="social-links">
                <a href="https://www.linkedin.com/in/jiteen-mohanty-61b814133" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-linkedin"></i> LinkedIn
                </a>
                <a href="https://github.com/JiteenMohanty" target="_blank" rel="noopener noreferrer">
                    <i className="fab fa-github"></i> GitHub
                </a>
                <a href="mailto:jiteen92718@gmail.com">
                    <i className="fas fa-envelope"></i> Email
                </a>
                <a href="#" onClick={openCvModal}>
                    <i className="fas fa-file-download"></i> Download CV
                </a>
            </div>

            {/* CV Preview Modal */}
            {isCvModalOpen && (
                <div className="cv-modal">
                    <div className="cv-modal-content">
                        <span className="cv-modal-close" onClick={closeCvModal}>&times;</span>
                        <h2>CV Preview</h2>
                        {/* You can embed a preview of your CV here */}
                        <iframe 
                            src="path-to-your-cv.pdf" 
                            width="100%" 
                            height="600px" 
                            title="CV Preview" 
                            frameBorder="0"
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
