import React, { useState } from "react";
import { Link } from "react-router-dom"; // Importing Link for navigation

const Home = () => {
  const [isCvModalOpen, setIsCvModalOpen] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const email = "jiteen92718@gmail.com";

  // Open the CV modal
  const openCvModal = () => {
    setIsCvModalOpen(true);
  };

  // Close the CV modal
  const closeCvModal = () => {
    setIsCvModalOpen(false);
  };

  // Handle mouse hover on email
  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  // Copy email to clipboard
  const copyToClipboard = () => {
    navigator.clipboard.writeText(email);
    alert("Email copied to clipboard!");
  };

  return (
    <>
      <div className="landing">
        <div className="overlay"></div>
        <div className="main-content">
          <h1>Hi, I'm Jiteen! a passionate Software Developer.</h1>
          <p>Hey there! fellow human.</p>
          <p>Explore my Projects and get to know me better.</p>
          <div className="social-links">
            <a
              href="https://www.linkedin.com/in/jiteen-mohanty-61b814133"
              className="linkedin"
            >
              <i className="fab fa-linkedin"></i> LinkedIn
            </a>
            <a href="https://github.com/JiteenMohanty" className="github">
              <i className="fab fa-github"></i> GitHub
            </a>
            {/* Modified Email */}
            <span
              className="email"
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              onClick={copyToClipboard}
            >
              <i className="fas fa-envelope"></i>
              {isHovered ? " Copy to clipboard" : " Email"}
            </span>

            {/* Update the CV link to open the modal */}
            <a href="#" className="cv" onClick={openCvModal}>
              <i className="fas fa-clipboard"></i> CV
            </a>
          </div>
        </div>

        {/* CV Modal */}
        {isCvModalOpen && (
          <div className="cv-modal">
            <div className="cv-modal-content">
              <span className="cv-modal-close" onClick={closeCvModal}>
                &times;
              </span>
              <iframe
                src="./Resume - final.pdf"
                width="100%"
                height="600px"
                title="CV Preview"
                frameBorder="0"
              />
            </div>
          </div>
        )}
      </div>

      <div className="gap"></div>

      <div className="navbar">
        <Link className="navbar-link" to="/about">
          About
        </Link>
        <Link className="navbar-link" to="/projects">
          Projects
        </Link>
      </div>
    </>
  );
};

export default Home;
