import React, { useState } from "react";
import { Link } from "react-router-dom"; // Importing Link for navigation

const Home = () => {
  const [isCvModalOpen, setIsCvModalOpen] = useState(false);

  const openCvModal = () => {
    setIsCvModalOpen(true);
  };

  const closeCvModal = () => {
    setIsCvModalOpen(false);
  };

  return (
    <>
      <div className="landing">
        <div className="overlay"></div>
        <div className="main-content">
            <h1>Hi, I'm Jiteen! a passionate Software Developer.</h1>
          <p>Hey there! fellow human.</p>
          <p>
            Explore my Projects and get to know me better.
          </p>
          <div class="social-links">
    <a href="https://github.com/JiteenMohanty" class="github">
        <i class="fab fa-github"></i> GitHub
    </a>
    <a href="mailto:jiteen92718@gmail.com" class="email">
        <i class="fas fa-envelope"></i> Email
    </a>
    <a href="#" class="cv">
        <i class="fas fa-clipboard"></i> CV
    </a>
</div>

        </div>
        {isCvModalOpen && (
          <div className="cv-modal">
            <div className="cv-modal-content">
              <span className="cv-modal-close" onClick={closeCvModal}>
                &times;
              </span>
              <h2>CV</h2>
              <iframe
                src="Resume - final.pdf"
                width="100%"
                height="600px"
                title="CV Preview"
                frameBorder="0"
              />
            </div>
          </div>
        )}
      </div>
      <div className="gap"></div>
      <div className="navbar">
        <Link className="navbar-link" to="/about">About</Link>
        <Link className="navbar-link" to="/projects">Projects</Link>
      </div>
    </>
  );
};

export default Home;
