import React, { useState } from "react";

const Home = () => {
  // State to control CV modal visibility
  const [isCvModalOpen, setIsCvModalOpen] = useState(false);

  // Function to open the CV preview modal
  const openCvModal = () => {
    setIsCvModalOpen(true);
  };

  // Function to close the CV preview modal
  const closeCvModal = () => {
    setIsCvModalOpen(false);
  };

  return (
    <><div className="landing-header">
          <div>
              <h1>Jiteen Mohanty</h1>
              <p>Software Developer</p>
              <div className="social-links">
                      <a
                          href="https://www.linkedin.com/in/jiteen-mohanty-61b814133"
                          target="_blank"
                          rel="noopener noreferrer"
                      >
                          <i className="fab fa-linkedin"></i> LinkedIn
                      </a>
                      <a
                          href="https://github.com/JiteenMohanty"
                          target="_blank"
                          rel="noopener noreferrer"
                      >
                          <i className="fab fa-github"></i> GitHub
                      </a>
                      <a href="mailto:jiteen92718@gmail.com">
                          <i className="fas fa-envelope"></i> Email
                      </a>
                      <a href="#">
                          <i className="fas fa-file-download"></i> Download CV
                      </a>
                  </div>
          </div>
          <div style={height="50px"}></div>
      </div><div className="landing">
              {/* Overlay to darken everything except .main-content */}
              <div className="overlay"></div>
              <div className="main-content">
                  <p>
                      Hey there! fellow human.
                  </p>
                  <p>
                      Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris
                      nisi ut aliquip ex ea commodo consequat.
                  </p>
                  <div className="explore-button-container">
                      <p>Explore my work and get to know me better!</p>
                      <a className="explore-button" href="#about">
                          About
                      </a>
                      <a className="explore-button" href="#projects">
                          Projects
                      </a>
                  </div>

                  
              </div>
              {/* CV Preview Modal */}
              {isCvModalOpen && (
                  <div className="cv-modal">
                      <div className="cv-modal-content">
                          <span className="cv-modal-close" onClick={closeCvModal}>
                              &times;
                          </span>
                          <h2>CV</h2>
                          {/* You can embed a preview of your CV here */}
                          <iframe
                              src="path-to-your-cv.pdf"
                              width="100%"
                              height="600px"
                              title="CV Preview"
                              frameBorder="0" />
                      </div>
                  </div>
              )}
          </div></>
  );
};

export default Home;
