import React, { useState } from 'react';
import './App.css';
import Home from './components/Home';
import About from './components/About';
import Projects from './components/Projects';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { SwitchTransition, CSSTransition } from 'react-transition-group';

const AnimatedRoutes = () => {
  const location = useLocation();
  const [dissolveActive, setDissolveActive] = useState(false);
  const nodeRef = React.useRef(null);

  const getTransitionClass = (pathname) => {
    if (pathname === '/about') return 'left';
    if (pathname === '/projects') return 'right';
    if (pathname === '/') return 'bottom';
    return '';
  };

  return (
    <>
      <SwitchTransition mode="out-in">
        <CSSTransition
          key={location.pathname}
          nodeRef={nodeRef}
          timeout={800}
          classNames={getTransitionClass(location.pathname)}
          onEnter={() => setDissolveActive(true)}
          onExited={() => setDissolveActive(false)}
          unmountOnExit
        >
          <div ref={nodeRef} className="transition-container">
            <Routes location={location}>
              <Route path="/" element={<Home />} />
              <Route path="/about" element={<About />} />
              <Route path="/projects" element={<Projects />} />
            </Routes>
          </div>
        </CSSTransition>
      </SwitchTransition>
      <div className={`dissolve-overlay ${dissolveActive ? 'active' : ''}`} />
    </>
  );
};

const App = () => {
  return (
    <div className="app-container">
      <main className="main-content">
        <Router>
          <AnimatedRoutes />
        </Router>
      </main>
      <div className="overlay"></div>
    </div>
  );
};

export default App;
